mapper.select:
select api_id, servicer_id, access_date, request, error_detail, http_status from axs_history where
api_id = #{apiId}
and servicer_id = #{servicerId}
and access_date >= #{historyReferenceDateFrom, typeHandler=com.spatialid.app.common.convert.StrToTimestampTypeHandler}
and access_date <= #{historyReferenceDateTo, typeHandler=com.spatialid.app.common.convert.StrToTimestampTypeHandler}
and http_status = #{httpStatus}
order by access_date desc, servicer_id asc, api_id asc
